var helper = require('helper-ex'),
    fs = require('fs'),
	  config = exports;

config.db = {
	url: '',
	name: '',
	getUrl: function () {
		return config.db.url + '/' + config.db.name;
	}
};

config.admin = {
  username: 'admin',
  password: 'admin'
};

config.room = {
  controller: null
};

config.configure = function (options) {
  if(typeof options == 'string') {
    // Load data using file location
    fs.readFile(options, function (err, data) {
      if(err) throw err;
      config.configure(JSON.parse(data));
    });
  } else {
    // Load given object
    helper.extend(config, options); 
  }
};