function Template() {
  
};

module.exports = Template;

/**
 * Compiles html/template text into a function.
 * 
 * @param {String} text The text to parse.
 */
Template.compile = function (text) {
  return function(params) {
    text = text.replace('##socket.io', params.socketio);
    text = text.replace('##url', params.url);
    
    return text;
  };
};